const Card = ({ title, description, logo }) => (
  <div
  /*   className="container" */
    style={{
      backgroundImage: `url(${fondoImg})`, //usa url contante declarada
    }}
  >

<div class="container-fluid">
  <div class="row">
    <div class="col-sm-6 text-start" className="info_card">
      <h1>{title || "DRINK NAME"}</h1>
      <p>{description || "description of the new drink for the website"}</p>
    </div>

    <div class="col-sm-6">
      <img src={logo || imgD}  alt="img" />
    </div>

    </div>
    </div>



  </div>
);

export default Card;

