import React from "react";
import "../styles/NewForm.css";

class DrinkNew extends React.Component {


  render() {
    const { onChange, onSubmit, form } = this.props;
    return (
      <div>
        <div>
          <form onSubmit={onSubmit}>
            <div>
              <label>TITULO:</label>
              <input
                className="inputForm"
                type="text"
                autoComplete="off" 
                placeholder="title"
                name="title"
                onChange={onChange}
                value={form.title}
              />
            </div>

            <div>
              <label>DESCRIPCION:</label>
              <input
                className="inputForm"
                type="text"
                autoComplete="off" 
                placeholder="description"
                name="description"
                onChange={onChange}
                value={form.description}
              />
            </div>
            <div>
              <label>URL ICONO:</label>
              <input
                className="inputForm"
                type="text"
                autoComplete="off" 
                placeholder="icono url"
                name="logo"
                onChange={onChange}
                value={form.logo}
              />
            </div>

            <div>
              <button type="submit">Submit</button>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

export default DrinkNew;
