import React from "react";
import Card from "./Card";

const CardList = ({exercice}) => (
    <div>
      {exercice.map((card) => {
        return (
          <Card
            key={card.id}//el navegador me pide que tengan el id
            title={card.title}
            description={card.description}
            img={card.imgIcono}
          />
        );
      })}
    </div>  
)

