import React from "react";
import fondoImg from "../images/fondo-violet.jpg"; //constante del fondo
import imgD from "../images/empy.png";
import "../styles/Card.css";

const Card = ({ title, description, logo }) => (
  <div
  /*   className="container" */

    style={{
      backgroundImage: `url(${fondoImg})`, //usa url contante declarada
    }}
  >
    <div className="info_card" class="text-start">
      <h1>{title || "DRINK NAME"}</h1>
      <p>{description || "description of the new drink for the website"}</p>
      <img src={logo || imgD} class="rounded float-end" alt="img" />
    </div>
  </div>
);

export default Card;

