import React from "react";
import CardList from "../components/CardList";
import Nav from "../components/Nav";
import AddButton from "../components/AddButton";
import Loading from "../components/Loading";
import Error from "./500";
import Footer from "../components/Footer";

import url from "../config";

class AllDrinksContainer extends React.Component {
  state = {
    data: [],
    loading: true,
    error: null,
  };

  async componentDidMount() {
    await this.fetchDrinks();
  }

  fetchDrinks = async () => {
    try {
      let res = await fetch(url + "/api/drink");
      let data = await res.json();

      this.setState({
        data,
        loading: false,
      });
    } catch (error) {
      this.setState({
        loading: false,
        error,
      });
    }
  };

  render() {
    if (this.state.loading) {
      return (
        <div>
          <Loading />
        </div>
      );
    }
    if (this.state.error) {
      return <Error />;
    }

    return (
      <React.Fragment>
        <Nav username="gabriel" />
        <div className="container-fluid">
          <div className="row row-cols-sm-1 row-cols-md-2">
            <CardList exercice={this.state.data} />
          </div>
        <AddButton />
        </div>
        <Footer />
      </React.Fragment>
    );
  }
}

export default AllDrinksContainer;
