    state={
      data:[],
      loading: true,
      error: null
    }

    async componentDidMount(){
      await this.fetchDrinks();
    }

    fetchDrinks = async () => {
      try{
        let res =  await fetch('http://localhost:8000/api/drink');
        let data= await res.json();

              this.setState({
                data,
                loading:false
              })
      }catch(e){

      }
    }

  render() {
    if(this.state.loading){
      return <Loading/>
    }
    return (
      <div>
        <Nav username="gabriel" />
        <CardList exercice={this.state.data} />
        <AddButton />
      </div>
    );
  }
}

export default Home;
