  <React.Fragment>
    <Nav username="gabriel" />

          <div className="row row-cols-1 row-cols-sm-1 row-cols-md-2">


      <div className="col">
      <h1>vista previa:</h1>
        <Card {...form} />
      </div>
      <div className="col">
        <FormDrink onChange={onChange} onSubmit={onSubmit} form={form} />
      </div>
    </div>

    <Footer />
  </React.Fragment>
);
export default DrinksNew;
