import React from "react";
import Nav from "../components/Nav";
import FormDrink from "../components/FormDrink";
import "../styles/NewForm.css";

class DrinkNew extends React.Component {

  state={
    form:{

    }
}

  handleChange = e=> {//evento de captura los cambio en los inputs
    this.setState({
      form:{
        ...this.state.form,
        [e.target.name]: e.target.value
      }
    });
  };


  render() {
    return (
      <div>
        <Nav username="gabriel"/>
        <FormDrink
          onChange={this.handleChange}
          from={ this.state.form }
        />
      </div>
    );
  }
}

export default DrinkNew;
