import "../styles/NewForm.css";

class DrinkNew extends React.Component {
  state = {
    form: {},
  };

  handleChange = (e) => {
    //evento de captura los cambio en los inputs
    this.setState({
      form: {
        ...this.state.form,
        [e.target.name]: e.target.value,
      },
    });
  };

  render() {
