import React from "react";
import Nav from "../components/Nav";
import FormDrink from "../components/FormDrink";
import Card from "../components/Card";
import "../styles/NewForm.css";
import Error from "./500";
import DrinkNew from "./DrinkNew";

class DrinkNewContainer extends React.Component {

  state = {
    form: {
      title: "",
      description: "",
      logo: "",
    },
    loading: false,
    error: null,
  };

  handleChange = (e) => {
    //evento de captura los cambio en los inputs
    this.setState({
      form: {
        ...this.state.form,
        [e.target.name]: e.target.value,
      },
    });
  };

  handleSubmit = async (e) => {
    this.setState({
      loading: true
    });

    e.preventDefault();

    try {
      let config = {
        method: "POST",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
        body: JSON.stringify(this.state.form),
      };

      let res = await fetch("http://localhost:8000/api/drink", config);
      let json = await res.json();

      this.setState({
        loading: false
      });
      this.props.history.push("../home");

    } catch (error) {
      console.log("en catch");
      this.setState({
        loading: false,
        error,
      });
    }

    console.log(this.state);
  };

  render() {
    if (this.state.error) {
      return <Error />;
    }
    return (
      <React.Fragment>
        <Nav username="gabriel" />

        <div className="cont">
          <h1>vista previa:</h1>
          <div>
            <Card {...this.state.form} />
          </div>
          <div>
            <FormDrink
              onChange={this.handleChange}
              onSubmit={this.handleSubmit}
              form={this.state.form}
            />
          </div>
        </div>
        </React.Fragment>
    );
  }
}

export default DrinkNewContainer;
